import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

const StripeButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey =
    "pk_test_51IUdzaHpPmQXLZj1MZJUrF3DdRYhYX0rhe1WI4dsj6q5tbGP2Bj4ab4EjXB582z1k6pBTsQHc97SUYaWyQQwZ5fW007pm2QKE1";
  //TODO Check why adding this new code block, breaks down the project
  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: priceForStripe,
        token,
      },
    })
      .then((response) => alert("Payment Succesful"))
      .catch((error) => console.log("Payment error", JSON.parse(error)));
  };
  return (
    <StripeCheckout
      label="Pay Now"
      name="CRWN Clothing Ltd."
      billingAddress
      shippingAddress
      image="https://svgshare.com/i/CUz.svg"
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeButton;
